package Window;

public class MyPoint {
	public int x;
	public int y;
	
	public MyPoint(int x,int y) {
		// TODO Auto-generated constructor stub
		this.x = x;
		this.y = y;
	}

	public void setY(int y2) {
		// TODO Auto-generated method stub
		this.y = y2;
	}

	public void setX(int x2) {
		// TODO Auto-generated method stub
		this.x = x2;
	}
	
	public int getX(){
		return this.x;
	}
	
	public int getY(){
		return this.y;
	}
}
